import React from 'react'
import { Button, Container, Grid, Typography } from '@material-ui/core'
import {useQuery, gql} from '@apollo/client'

const LOGIN = gql`
	query{
		login(email: "", password: ""){
			accessToken
		}
	}
`

const Header = ({loggedIn}) => {

	const handleLogOut = () => {

		fetch('http://localhost:3000/api/authentication/logout')
			.then((response) => response.json())
			.then((response) => {
				window.location.href = '/login';
			})
	}

	const handleLogin = () => {
		// const response = useQuery("");
	}

	return (
		<header id="header">

			<Container component="main" justify="center" maxWidth="lg">
				<Grid container direction="row" alignItems="stretch" spacing={3}>
					<Grid item justify="flex-end">
						<div id="text-logo" onClick={() => window.location.href = "/"} style={{cursor: "pointer"}}>
							<Typography variant="h5" >Youtube Downloader</Typography>
						</div>
					</Grid>
					<Grid item md={6} xs={6} lg={6}></Grid>
					<Grid item>
						{loggedIn ?
							(<Button variant="contained" color="primary" onClick={handleLogOut} size="small">Logout</Button>) :
							<Button variant="contained" color="primary" onClick={handleLogin} size="small" href="/login">Login</Button>}
					</Grid>
				</Grid>
			</Container>
		</header>
	)
}

export default Header
