import Header from "./Header"
import React, { useState } from 'react'
import { Button, TextField, Typography, Container } from '@material-ui/core';
import Alert from '@material-ui/lab/Alert';
import useLoginQuery from "./managers/queries/useLoginQuery";



const Login = () => {
	const [user, setUser] = useState("");
	const [password, setPassword] = useState("");
	const [errorMessage, setErrorMessage] = useState("");

	const handleLoginClick = (e) => {
		e.preventDefault();
		if (user.length === 0 || password.length === 0) {
			setErrorMessage("Valorizzare tutti i campi");
		} else {
			const loginQuery = useLoginQuery();
			fetch(`${process.env.GRAPQHL_URI}/authentication/login`, {
				method: "POST",
				headers: {
					'Content-Type': 'application/json',
					'accept': 'application/json'
				},
				body: JSON.stringify({
					query: loginQuery,
					variables: {email: user, password} 
				})
			}).then(res => res.json().then((res) => {
				console.log(JSON.stringify(res));
				if ("errorMessage" in res) {
					setErrorMessage(res.errorMessage);
				} else if ("logged" in res && res.logged) {
					/* Cookies.set("token",res.token,{
							expires: 60*60
					}) */
					window.location = '/';
				}
			}));
		}
	}
	var displayErrorMessage = <div></div>;
	if (errorMessage.length > 0) {
		displayErrorMessage = <Alert
			severity="error"
			style={{ "marginTop": "10px" }}>
			{errorMessage}
		</Alert>
	}

	return (
		<div>
			<Header />
			<div style={{ marginTop: "5%" }}>
				<Container component="main" maxWidth="xs">
					{errorMessage.length > 0 ? <Alert
						severity="error"
						style={{ "marginTop": "10px" }}>
						{errorMessage}
					</Alert> :
						<div></div>}
					<div>
						<Typography component="h1" variant="h5">
							Sign in
                        </Typography>
						<form noValidate method="POST">
							<TextField
								variant="outlined"
								margin="normal"
								required
								fullWidth
								id="user"
								label="Username"
								name="user"
								autoComplete="userName"
								autoFocus
								onChange={(e) => setUser(e.target.value)}
							/>
							<TextField
								variant="outlined"
								margin="normal"
								required
								fullWidth
								name="password"
								label="Password"
								type="password"
								id="password"
								autoComplete="current-password"
								onChange={(e) => { setPassword(e.target.value) }}
							/>
							<Button
								type="submit"
								fullWidth
								variant="contained"
								color="primary"
								style={{ marginTop: "10px" }}
								onClick={handleLoginClick}
							>
								Sign In
                            </Button>
							{/* Show/Hide error message */}
							{/* {displayErrorMessage} */}
						</form>
					</div>
				</Container>
			</div>
		</div>
	)
}

export default Login
