import React, { useContext } from 'react'
import { Button, Grid, Typography } from '@material-ui/core'
import UserProvider from '../providers/UserProvider'
import { Link } from 'react-router-dom';

const Header = () => {

	const { user } = useContext(UserProvider);

	console.log("user context ", user);

	const handleLogOut = () => {

		fetch('http://localhost:3000/api/authentication/logout')
			.then((response) => response.json())
			.then((response) => {
				window.location.href = '/login';
			})
	}

	//TODO
	//handleLogoutClick()

	return (
		<header id="header">
			<Grid container direction="row" justify="center" alignItems="stretch" spacing={3}>
				<Grid item>
					<Button color="primary" 
							color=""
							to="/join" component={Link}>
							Join a Room
					</Button>
					<div id="text-logo"style={{ cursor: "pointer" }}>
						<Link href="/">Youtube Downloader</Link>
					</div>
				</Grid>
				<Grid item md={6} xs={6} lg={6}></Grid>
				<Grid item md={1} xs={1} lg={1}><Typography variant="h6">user:{user}</Typography></Grid>
				<Grid item>
					{user ?
						(<Button variant="contained" color="primary" onClick={handleLogOut} size="small">Logout</Button>) :
						<Button variant="contained" color="primary" size="small" href="/login">Login</Button>}
				</Grid>
			</Grid>
		</header>
	)
}

export default Header
