import React from 'react'
import {Button, Grid} from '@material-ui/core'

const Header = ({ loggedIn }) => {

	const handleLogOut = () => {

		fetch('http://localhost:3000/api/authentication/logout')
			.then((response) => response.json())
			.then((response) => {
				window.location.href = '/login';
			})
	}

	const drawLogInOutButtons = () => {
		if (loggedIn) {
			return <Button
						className="inline-flex items-center bg-white-100 border-2 border-solid border-red-500 py-1 px-3 focus:outline-none hover:bg-red-500 hover:text-white rounded text-base mt-4 md:mt-0"
						onClick={handleLogOut}
					>
						<a>Log Out</a>
					</Button>;
		} else {
			return <Button className="inline-flex items-center bg-gray-100 border-0 py-1 px-3 focus:outline-none hover:bg-gray-200 rounded text-base mt-4 md:mt-0">
				<a href="/login">Log In</a>
				<svg fill="none" stroke="currentColor" strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" className="w-4 h-4 ml-1" viewBox="0 0 24 24">
					<path d="M5 12h14M12 5l7 7-7 7"></path>
				</svg>
			</Button>;
		}

	}

	return (
		<header>
			<Grid container direction="row" justify="center" alignItems="stretch" spacing={3}>
				<Grid item justify="flex-end">
					{loggedIn ? 
						(<Button variant="contained" color="primary" onClick={handleLogOut} size="sm">Logout</Button>) : 
						<Button variant="contained" color="primary" size="sm">Login</Button>}
				</Grid>
				<Grid item>
					{loggedIn ? 
						(<Button variant="contained" color="primary" onClick={handleLogOut} size="sm">Logout</Button>) : 
						<Button variant="contained" color="primary" size="sm">Login</Button>}
				</Grid>
				<Grid item>
					{loggedIn ? 
						(<Button variant="contained" color="primary" onClick={handleLogOut} size="sm">Logout</Button>) : 
						<Button variant="contained" color="primary" size="sm">Login</Button>}
				</Grid>
			</Grid>
		</header>
	)
}

export default Header
