import Header from "../components/Header"
import Head from 'next/head'
import React, { useState } from 'react'
import { Button, TextField, makeStyles, Typography, Link, Grid, Container } from '@material-ui/core';
import Alert from '@material-ui/lab/Alert';
import Cookies from 'js-cookie'

const useStyles = makeStyles(theme => {
	return {
		root: {
			'& > *': {
				margin: theme.spacing(1),
				width: '25ch',
			}
		}
	}
});

export const getStaticProps = async (context) => {

	return {
		props: {

		}
	}


}

const login = () => {
	const classes = useStyles();
	const [user, setUser] = useState("");
	const [password, setPassword] = useState("");
	const [errorMessage, setErrorMessage] = useState("");
	//const cookie = new Cookies();

	const clearErrorMessage = () => {
		if (errorMessage.length > 0)
			setErrorMessage("");
	}

	const onChangeUser = (e) => {
		setUser(e.target.value)
		clearErrorMessage();
	};
	const onChangePassword = (e) => {
		setPassword(e.target.value);
		clearErrorMessage();
	};


	const handleLoginClick = (e) => {
		e.preventDefault();
		if (user.length === 0 || password.length === 0) {
			setErrorMessage("Valorizzare tutti i campi");
		} else {

			fetch("http://localhost:3000/api/authentication/login", {
				method: "POST",
				headers: {
					'Content-Type': 'application/json'
				},
				body: JSON.stringify({
					user: user,
					password: password
				})
			}).then(res => res.json().then((res) => {
				console.log(JSON.stringify(res));
				if ("errorMessage" in res) {
					setErrorMessage(res.errorMessage);
				} else if ("logged" in res && res.logged) {
					/* Cookies.set("token",res.token,{
							expires: 60*60
					}) */
					window.location = '/';
				}
			}));
		}
	}
	var displayErrorMessage = <div></div>;
	if (errorMessage.length > 0) {
		displayErrorMessage = <Alert
			severity="error"
			style={{ "marginTop": "10px" }}>
			{errorMessage}
		</Alert>
	}
	return (
		<div>
			<Head>
				<title>Log In</title>
				<meta name="viewport" content="initial-scale=1.0, width=device-width" />
			</Head>
			<Header />
			<div style={{ marginTop: "5%" }}>
				<Container component="main" maxWidth="xs">

					<div className={classes.paper}>
						<Typography component="h1" variant="h5">
							Sign in
                        </Typography>
						<form className={classes.form} noValidate>
							<TextField
								variant="outlined"
								margin="normal"
								required
								fullWidth
								id="user"
								label="Username"
								name="user"
								autoComplete="userName"
								autoFocus
								onChange={onChangeUser}
							/>
							<TextField
								variant="outlined"
								margin="normal"
								required
								fullWidth
								name="password"
								label="Password"
								type="password"
								id="password"
								autoComplete="current-password"
								onChange={onChangePassword}
							/>
							<Button
								type="submit"
								fullWidth
								variant="contained"
								color="primary"
								className={classes.submit}
								style={{ marginTop: "10px" }}
								onClick={handleLoginClick}
							>
								Sign In
                            </Button>
							{/* Show/Hide error message */}
							{displayErrorMessage}
							<Grid container style={{ "marginTop": "10px" }}>
								<Grid item xs>
									{/* <Link href="#" variant="body2">
                                        Forgot password?
                                    </Link> */}
								</Grid>
								<Grid item>
									<Link href="/signup" variant="body2">
										{"Don't have an account? Sign Up"}
									</Link>
								</Grid>
							</Grid>
						</form>
					</div>
				</Container>
			</div>
		</div>
	)
}

export default login
