import { useReducer } from 'react'
function User() {

    const userReducer = (state, action) => {
        switch (action.type) {
            case 'login':
                return {
                    isLoggedIn: true,
                    username: action.payload,
                    lastModified: Date.now()
                }
            case 'logout':
                return {
                    isLoggedIn: false,
                    username: '',
                    lastModified: Date.now()
                }
            default:
                return state
        }
    }
    const initialValue = {
        isLoggedIn: false,
        userName: "",
        lastModified: new Date()
    }

    const [stateUser, dispatchUser] = useReducer(userReducer, initialValue)


    return { stateUser, dispatchUser }
}


export default User
