import React from 'react'
import ReactDOM from 'react-dom'

const Header = ({ loggedIn }) => {

	const handleLogOut = () => {

		fetch('http://localhost:3000/api/authentication/logout')
			.then((response) => response.json())
			.then((response) => {
				window.location.href = '/login';
			})
	}

	const drawLogInOutButtons = () => {
		if (loggedIn) {
			return <button
								className="inline-flex items-center bg-white-100 border-2 border-solid border-red-500 py-1 px-3 focus:outline-none hover:bg-red-500 hover:text-white rounded text-base mt-4 md:mt-0"
								onClick={handleLogOut}
							>
								<a>Log Out</a>
							</button>;
		} else {
			return <button className="inline-flex items-center bg-gray-100 border-0 py-1 px-3 focus:outline-none hover:bg-gray-200 rounded text-base mt-4 md:mt-0">
				<a href="/login">Log In</a>
				<svg fill="none" stroke="currentColor" strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" className="w-4 h-4 ml-1" viewBox="0 0 24 24">
					<path d="M5 12h14M12 5l7 7-7 7"></path>
				</svg>
			</button>;
		}

	}

	return (
		<header className="text-gray-600 body-font">
			<div className="container mx-auto flex flex-wrap p-5 flex-col md:flex-row items-center">
				<a className="flex title-font font-medium items-center text-gray-900 mb-4 md:mb-0" href="/">
					<svg xmlns="http://www.w3.org/2000/svg" fill="none" stroke="currentColor" strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" className="w-10 h-10 text-white p-2 bg-indigo-500 rounded-full cursor-pointer" viewBox="0 0 24 24">
						<path d=""></path>
					</svg>
					<span className="ml-3 text-xl">Youtube Downloader</span>
				</a>
				<nav className="md:ml-auto flex flex-wrap items-center text-base justify-center">
					<a className="mr-5 hover:text-gray-900" href="/downloader">Downloader</a>
				</nav>


				{drawLogInOutButtons()}

			</div>
		</header>
	)
}

export default Header
