import React, {useState, useEffect, useContext} from 'react'
import { Button, Grid, Typography } from '@material-ui/core'
import UserProvider from '../providers/UserProvider'
import { Link } from 'react-router-dom';

const Header = () => {

	const { userContext, setUserContext } = useContext(UserProvider);
	const { user, setUserPage } = useState("");

	useEffect(() =>{
		console.log("userContext ", userContext);
		console.log("user ", user);
		if(userContext == ""){
			console.log("userContext is empty");
		} else{
			setUserPage(userContext);
		}
	}, [])



	const handleLogOut = () => {
		setUserContext(null);
	}

	return (
		<header id="header">
			<Grid container direction="row" justify="center" alignItems="stretch" spacing={3}>
				<Grid item>
					<div id="text-logo" style={{ cursor: "pointer" }}>
						<Link to="/" replace style={{ textDecoration: "none", fontSize: "20pt", color: "black", padding: "3px 5px 3px 5px" }}>Youtube Downloader</Link>
					</div>
				</Grid>
				<Grid item md={3} xs={3} lg={3}></Grid>
				{userContext ? <Grid align="right" item md={3} xs={3} lg={3}>
						<Typography variant="h6" align="right">
							<Link to="/downloader" replace style={{textDecoration: "none", color: "black", padding: "3px 5px 3px 5px" }}>Downloader </Link>
						</Typography>
						</Grid> : <div></div>}
				<Grid item md={2} xs={2} lg={2}>
					{userContext? (
						<Typography variant="h6" align="right">
						Welcome, {userContext}
					</Typography>
					): <div></div>}
				</Grid>
				<Grid item>
					{userContext ?
						(<Button variant="contained" color="primary" onClick={handleLogOut} size="small">Logout</Button>) :
						<Button variant="contained" color="primary" size="small" href="/login">Login</Button>}
				</Grid>
			</Grid>
		</header>
	)
}

export default Header
