import React from 'react'
import {Button, Grid, Typography} from '@material-ui/core'
import {BrowserRouter as Route, NavLink as Link} from 'react-router-dom'
// import Login from './Login'

const Header = ({ loggedIn }) => {

	const handleLogOut = () => {

		fetch('http://localhost:3000/api/authentication/logout')
			.then((response) => response.json())
			.then((response) => {
				window.location.href = '/login';
			})
	}

	return (
			<header id="header">
				<Grid container direction="row" justify="center" alignItems="stretch" spacing={3}>
					<Grid item justify="flex-end">
						<div id="text-logo">
							<Typography variant="h5" >Youtube Downloader</Typography>
						</div>
					</Grid>
					<Grid item md={6} xs={6} lg={6}></Grid>
					<Grid item>
						{loggedIn ? 
							(<Button variant="contained" color="primary" onClick={handleLogOut} size="sm">Logout</Button>) : 
							<Button variant="contained" color="primary" size="sm">
								<Link to={{
									pathname: "/login",
									search: "?sort=name",
									state: { fromHome: true }
								}}>Login</Link>
							</Button>}
					</Grid>
				</Grid>
			</header>
	)
}

export default Header
