import Header from "./Header"
import React, { useContext, useState } from 'react'
import { Button, TextField, Typography, Container } from '@material-ui/core';
import Alert from '@material-ui/lab/Alert';
import loginMutation from "./managers/mutations/login";
import UserProvider from "../providers/UserProvider";

const Login = () => {
	const [email, setEmail] = useState("");
	const [password, setPassword] = useState("");
	const [errorMessage, setErrorMessage] = useState("");

	const { setUser } = useContext(UserProvider);

	const handleLoginClick = (e) => {
		e.preventDefault();
		if (email.length === 0 || password.length === 0) {
			setErrorMessage("Valorizzare tutti i campi");
		} else {
			fetch('http://localhost:4000/graphql', {
				method: "POST",
				headers: {
					'Access-Control-Allow-Origin': '*',
					'Content-Type': 'application/json'
				},
				body: JSON.stringify({
					query: loginMutation,
					variables: { email: email, password: password }
				})
			}).then(res => {
				console.log("res", res);
				return res.json();
			}).then(res => {
				console.log(res);
				if (res.data?.login?.accessToken && res.data?.login?.username) {
					setUser(res.login.username);
					window.location.href = "/";
				} else {
					if (res.errors && res.errors.length > 0) {
						setErrorMessage(res.errors[0].message);
					}
				}
			}
			);
		}
	}
	var displayErrorMessage = <div></div>;
	if (errorMessage.length > 0) {
		displayErrorMessage = <Alert
			severity="error"
			style={{ "marginTop": "10px" }}>
			{errorMessage}
		</Alert>
	}

	return (
		<div>
			<Header />
			<div style={{ marginTop: "5%" }}>
				<Container component="main" maxWidth="xs">
					{errorMessage.length > 0 ? <Alert
						severity="error"
						style={{ "marginTop": "10px" }}>
						{errorMessage}
					</Alert> :
						<div></div>}
					<div>
						<Typography component="h1" variant="h5">
							Sign in
                        </Typography>
						<form noValidate method="POST">
							<TextField
								variant="outlined"
								margin="normal"
								required
								fullWidth
								id="email"
								label="Email"
								name="email"
								autoComplete="email"
								autoFocus
								onChange={(e) => setEmail(e.target.value)}
							/>
							<TextField
								variant="outlined"
								margin="normal"
								required
								fullWidth
								name="password"
								label="Password"
								type="password"
								id="password"
								autoComplete="current-password"
								onChange={(e) => { setPassword(e.target.value) }}
							/>
							<Button
								type="submit"
								fullWidth
								variant="contained"
								color="primary"
								style={{ marginTop: "10px" }}
								onClick={handleLoginClick}
							>
								Sign In
                            </Button>
							{/* Show/Hide error message */}
							{/* {displayErrorMessage} */}
						</form>
					</div>
				</Container>
			</div>
		</div>
	)
}

export default Login
