	// 		fetch("http://localhost:3000/api/authentication/login", {
	// 			method: "POST",
	// 			headers: {
	// 				'Content-Type': 'application/json'
	// 			},
	// 			body: JSON.stringify({
	// 				user: user,
	// 				password: password
	// 			})
	// 		}).then(res => res.json().then((res) => {
	// 			console.log(JSON.stringify(res));
	// 			if ("errorMessage" in res) {
	// 				setErrorMessage(res.errorMessage);
	// 			} else if ("logged" in res && res.logged) {
	// 				/* Cookies.set("token",res.token,{
	// 						expires: 60*60
	// 				}) */
	// 				window.location = '/';
	// 			}
	// 		}));
	// 	}
	// }
	var displayErrorMessage = <div></div>;
	if (errorMessage.length > 0) {
		displayErrorMessage = <Alert
			severity="error"
			style={{ "marginTop": "10px" }}>
			{errorMessage}
		</Alert>
	}
	return (
		<div>
			<Header />
			<div style={{ marginTop: "5%" }}>
				<Container component="main" maxWidth="xs">

					<div>
						<Typography component="h1" variant="h5">
							Sign in
                        </Typography>
						<form noValidate>
							<TextField
								variant="outlined"
								margin="normal"
								required
								fullWidth
								id="user"
								label="Username"
								name="user"
								autoComplete="userName"
								autoFocus
								onChange={onChangeUser}
							/>
							<TextField
								variant="outlined"
								margin="normal"
								required
								fullWidth
								name="password"
								label="Password"
								type="password"
								id="password"
								autoComplete="current-password"
								onChange={onChangePassword}
							/>
							<Button
								type="submit"
								fullWidth
								variant="contained"
								color="primary"
								style={{ marginTop: "10px" }}
								onClick={handleLoginClick}
							>
								Sign In
                            </Button>
							{/* Show/Hide error message */}
							{displayErrorMessage}
							<Grid container style={{ "marginTop": "10px" }}>
